twaver.Util.registerImage('layer', {
  rel: true,
  origin: {
    x: 0,
    y: 0
  },
  v: [
    {
      shape: 'rect',
      x: 0,
      y: 0,
      w: 1,
      h: 1,
      r: 5,
      fill: function (data, view) {
        if (data.getClient('focus')) {
          return 'rgba(255, 165, 0, 0.2)';
        } if (view.isSelected(data)) {
          return 'rgba(244, 247, 250, 0.6)';
        } else {
          return 'rgba(0, 0, 0, 0)';
        }
      },
      lineWidth: 2,
      lineColor: function (data, view) {
        if (data.getClient('focus')) {
          return '#FFA500';
        } else {
          return '#CBD1D9';
        }
      },
      lineDash: [8, 8]
    },
    {
      shape: 'rect',
      x: 20,
      y: -10,
      w: 100,
      h: 20,
      r: 10,
      fill: 'white',
      lineWidth: 2,
      lineColor: function (data, view) {
        if (data.getClient('focus')) {
          return '#FFA500';
        } else {
          return '#CBD1D9';
        }
      }
    },
    {
      shape: 'text',
      text: function (data) {
        return data.getClient('title');
      },
      x: 70,
      y: 0
    }
  ]
});

twaver.Util.registerImage('border', {
  rel: true,
  origin: {
    x: 0,
    y: 0
  },
  w: 100,
  h: 100,
  v: [
    {
      shape: 'rect',
      x: 0,
      y: 0,
      w: 1,
      h: 1,
      r: 10,
      fill: function (data, view) {
        if (data.getClient('focus')) {
          return 'rgba(255, 165, 0, 0.2)';
        } else {
          return 'rgba(230, 230, 230, 0.6)';
        }
      },
      visible: function (data, view) {
        return data.getClient('focus') || view.isSelected(data);
      },
      lineColor: function (data, view) {
        if (data.getClient('focus')) {
          return 'rgba(255, 165, 0, 1)';
        } else {
          return 'rgba(0, 0, 0, 0)';
        }
      },
      lineWidth: 2
    }
  ]
});

